import React,{useState} from 'react'

export const App = () => {
  const [count, setCount] = useState(0)

  const aumentar = () => {
    setCount(count + 1);
  }

  const disminuir = () => {
    setCount(count - 1);
  }

  const reiniciar = () => {
    setCount(0);
  }

  return (
    <div>
      <button onClick={aumentar}>Aumentar</button>
      <button onClick={disminuir}>Disminuir</button>
      <button onClick={reiniciar}>Reiniciar</button>
      <h1>{count}</h1>
    </div>
  )
}

export default App;
