import React from 'react'
import {useState} from 'react'

function App() {
  const reyes = [
    {
      nombre: 'Atanagildo',
      reinado : 15,
      vacasComidas : 9
    },
    {
      nombre: 'Ervigio',
      reinado: 7,
      vacasComidas: 3,
    },
    {
      nombre: 'Ataúlfo',
      reinado: 5,
      vacasComidas: 16,
    },
    {
      nombre: 'Leovigildo',
      reinado: 18,
      vacasComidas: 3
    },
    {
      nombre: 'Sisebuto',
      reinado: 19,
      vacasComidas: 13
    },
    {
      nombre: 'Recesvinto',
      reinado: 19,
      vacasComidas: 11
    },
    {
      nombre: 'Teodorico',
      reinado: 33,
      vacasComidas: 12
    }
  ]

  const [source, setSource] = useState()

  const resultado = reyes.map(valor => {
    setSource('www.html6.es/img/rey.' + valor.nombre.toLowerCase() + '.png')
    return <div key={valor.nombre} className='rey'>
      <div className='nombre_rey' key={valor.nombre.toUpperCase}></div>
      <div> ha comido {valor.reinado * valor.vacasComidas} en sus ${valor.reinado} años de reinado</div>
      <img src= {source}/>
    </div>;
  });
  return (
    <>
      <div className='caja'>
        {resultado}
      </div>
    </>
  )
}

export default App
