import React from 'react'
import {useState} from 'react'

function App() {
  const reyes = [
    {
      nombre: 'Atanagildo',
      reinado : 15,
      vacasComidas : 9
    },
    {
      nombre: 'Ervigio',
      reinado: 7,
      vacasComidas: 3,
    },
    {
      nombre: 'Ataúlfo',
      reinado: 5,
      vacasComidas: 16,
    },
    {
      nombre: 'Leovigildo',
      reinado: 18,
      vacasComidas: 3
    },
    {
      nombre: 'Sisebuto',
      reinado: 19,
      vacasComidas: 13
    },
    {
      nombre: 'Recesvinto',
      reinado: 19,
      vacasComidas: 11
    },
    {
      nombre: 'Teodorico',
      reinado: 33,
      vacasComidas: 12
    }
  ]

  const [total_vacas, setTotalVacas] = useState(0);
  const [nombre, setNombre] = useState('');
  const [nombre_may, setNombreMay] = useState('');
  const [source, setSource] = useState('');
  const [output, setOutput] = useState('');
  var resultado1 = reyes.map((valor) => {
    setTotalVacas(valor.vacasComidas * valor.reinado);
    setNombre(valor.nombre);
    setNombreMay(nombre.toUpperCase)
    setSource('https://www.html6.es/img/rey_' + valor.nombre.toLowerCase + '.png');
    setOutput(`<div className='rey'>
      <div className='nombre_rey'>${nombre}</div>
      <div> ha comido ${total_vacas} en sus ${valor.reinado} años de reinado</div>
      <img src='${source}'>
    </div>`);
  });
  console.log(resultado1)
  return (
    <>
      <div className='caja'>
      {resultado1[0]}
      </div>
    </>
  )
}

export default App
