import React from 'react'
import {useState} from 'react'

function App() {
  const reyes = [
    {
      nombre: 'Atanagildo',
      reinado : 15,
      vacasComidas : 9
    },
    {
      nombre: 'Ervigio',
      reinado: 7,
      vacasComidas: 3,
    },
    {
      nombre: 'Ataúlfo',
      reinado: 5,
      vacasComidas: 16,
    },
    {
      nombre: 'Leovigildo',
      reinado: 18,
      vacasComidas: 3
    },
    {
      nombre: 'Sisebuto',
      reinado: 19,
      vacasComidas: 13
    },
    {
      nombre: 'Recesvinto',
      reinado: 19,
      vacasComidas: 11
    },
    {
      nombre: 'Teodorico',
      reinado: 33,
      vacasComidas: 12
    }
  ]

  const [total_vacas, setTotalVacas] = useState(0);
  const [nombre, setNombre] = useState('');
  const [source, setSource] = useState('');
  const [output, setOutput] = useState('');
  const resultado = reyes.map(valor => {
    setTotalVacas(valor.vacasComidas * valor.reinado);
    setNombre(valor.nombre.toUpperCase());
    setSource('https://www.html6.es/img/rey_' + valor.nombre.toLowerCase + '.png');
    <div key={valor.nombre} className='rey'>
      <div className='nombre_rey' key={nombre}></div>
      <div> ha comido {total_vacas} en sus ${valor.reinado} años de reinado</div>
      <img src= {source}/>
    </div>;
  })
  console.log(resultado);
  return (
    <>
      <div className='caja'>
        {resultado}
      </div>
    </>
  )
}

export default App
