import React from 'react'
import {useState} from 'react'

function App() {
  const reyes = [
    {
      nombre: 'Atanagildo',
      reinado : 15,
      vacasComidas : 9
    },
    {
      nombre: 'Ervigio',
      reinado: 7,
      vacasComidas: 3,
    },
    {
      nombre: 'Ataúlfo',
      reinado: 5,
      vacasComidas: 16,
    },
    {
      nombre: 'Leovigildo',
      reinado: 18,
      vacasComidas: 3
    },
    {
      nombre: 'Sisebuto',
      reinado: 19,
      vacasComidas: 13
    },
    {
      nombre: 'Recesvinto',
      reinado: 19,
      vacasComidas: 11
    },
    {
      nombre: 'Teodorico',
      reinado: 33,
      vacasComidas: 12
    }
  ]

  const resultado = reyes.map(valor => {
    const source = 'https://www.html6.es/img/rey.' + valor.nombre.toLowerCase() + '.png';
    const total_vacas = valor.reinado * valor.vacasComidas;
    return <div key={valor.nombre} className='rey'>
      <div><div className='nombre_rey'>{valor.nombre.toUpperCase}</div> 
      ha comido {total_vacas} en sus {valor.reinado} años de reinado</div>
      <img src= {source}/>
    </div>;
  });
  return (
    <>
      <div className='caja'>
        {resultado}
      </div>
    </>
  )
}

export default App
