import React, {useEffect, useState} from 'react';

function App() {
  const [nombre, setNombre] = useState('');
  const [imagen, setImagen] = useState('');
  useEffect(() => {
    const url = 'https://randomuser.me/api';
    const peticion = fetch(url);
    peticion
    .then(datos => datos.json())
    .then(lectura => {
      console.log(lectura.results[0].name.first);
      setNombre(lectura.results[0].name.first);
      setImagen(lectura.results[0].picture.large);
    })
    .catch(() => console.log('Se ha producido un Error'));
  },[]);

  return (
    <>
    <h1>Empleado/a del mes</h1>
    {nombre}
    <div><img src={imagen}></img></div>
    </>
  )
}

export default App
